"use strict";
// variabel
// 1. variabel let cuma bisa diakses pada blok tertentu
// 2. variabel var bisa diakses di manapun
// 3. variabel const sifatnya sama seperti variabel var tetapi nilainya tidak bisa diubah
let namaBapak = 'Mulyono';
namaBapak = 'fufufafa';
console.log(namaBapak);
var age = 54;
age = 32;
console.log(age);
const asalsekolah = "Losaran";
// asalsekolah = "uiui"
console.log(asalsekolah);
